import React, { useEffect, useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid } from '@material-ui/core';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import { useTranslation } from 'react-i18next';
import { MedContext } from '../internalMedContext';

const useStyles = makeStyles((theme) => ({
    table: {
        minWidth: 650,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: 'black',
        marginBottom: 10,
        marginTop: 10,
        fontSize: 12,

    },
    h4: {
        fontSize: 20,
        textAlign: "center"
    }
}));

function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
}



const ViewInternalMed = () => {
    const { t, i18n } = useTranslation();
    const classes = useStyles();
    const [values, setValues] = useContext(MedContext);

    const rows = [
        createData('name', values.name),
        createData('Date Of Birth', values.DateOfBirth.toLocaleDateString()),
        createData('Height', values.height),
        createData('Weight', values.weight),
        createData('Sex', values.sex),
    ];

    const rows1 = [
        createData('Food allergis', values.allergis.foods),
        createData('Medcine allergis', values.allergis.medcine),
    ];

    const rows2 = [
        createData('stools', values.stools),
        createData('stoolfrequency', values.stoolfrequency),
    ]

    const rows3 = [
        createData('Place symptoms', values.symptomoccur),
    ]

    return (
        <div>
            <Container component="main" maxWidth="md">
                <Grid item xs={12}>
                    <strong className={classes.h4}><h4 >{t('internalMedcine.PersonnalInformation')}</h4></strong>
                </Grid>

                <TableContainer component={Paper} >
                    <Table className={classes.table} aria-label="simple table">

                        <TableBody>
                            {rows.map((row) => (
                                <TableRow key={row.name}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="right">{row.calories}</TableCell>

                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>

                <Grid item xs={12}>
                   <strong className={classes.h4}><h4>{t('internalMedcine.Allergie')} /アレルギー</h4></strong>
                </Grid>
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">

                        <TableBody>
                            {rows1.map((row) => (
                                <TableRow key={row.name}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="right">{row.calories}</TableCell>

                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>

                <Grid item xs={12}>
                   <strong className={classes.h4}><h4>All that apply to your stool</h4></strong>
                </Grid>
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">

                        <TableBody>
                            {rows2.map((row) => (
                                <TableRow key={row.name}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="right">{row.calories}</TableCell>

                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>

                <Grid item xs={12}>
                    <strong className={classes.h4}><h4>Place where you experience symptom</h4></strong>
                </Grid>
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">

                        <TableBody>
                            {rows3.map((row) => (
                                <TableRow key={row.name}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="right">{row.calories}</TableCell>

                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Container>
        </div>
    )
}
export default ViewInternalMed;
